-- ----------------------------
-- Table structure for colum
-- ----------------------------
DROP TABLE IF EXISTS `colum`;
CREATE TABLE `colum`  (
  `columId` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `content` varchar(20000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `orgId` int(11) NULL DEFAULT NULL,
  `creatTime` datetime(0) NULL DEFAULT NULL,
  `orderNum` int(11) NULL DEFAULT NULL,
  `styleId` int(11) NULL DEFAULT NULL,
  `issueId` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`columId`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 22 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
