INSERT INTO `issue` VALUES (1, '931fbb5c-f7c3-4f1f-908b-5f53e3f09f7cwallpaper1.jpg', '第十七届中国机器学习会议', '<p style=\"text-indent:2em;\">\n	<span style=\"color:#222222;font-family:Consolas, &quot;white-space:pre-wrap;background-color:#FFFFFF;\">第十七届中国机器学习会议（CCML 2019）由中国人工智能学会和中国计算机学会联合主办，中国人工智能学会机器学习专业委员会和中国计算机学会人工智能与模式识别专业委员会协办，贵州大学承办。该系列会议每两年举行一次，现已成为国内机器学习界最主要的学术活动。此次会议将为机器学习及相关研究领域的学者交流最新研究成果、进行广泛的学术讨论提供便利，并且将邀请国内机器学习领域的著名学者做精彩报告。</span>\n</p>', '2020-06-11 19:15:29', '{\"streetNumber\":\"\",\"street\":\"\",\"district\":\"花溪区\",\"city\":\"贵阳市\",\"province\":\"贵州省\",\"lng\":106.632888,\"lat\":26.404925,\"issueId\":1}', 16);
INSERT INTO `issue` VALUES (21, 'a3adc98b-7511-4fb2-9fc5-6304ddfa48bcimage01.jpg', '标题1', '<p>内容1</p>', '2020-04-13 05:50:38', '{\"streetNumber\":\"84号\",\"street\":\"王家塘街\",\"district\":\"青羊区\",\"city\":\"成都市\",\"province\":\"四川省\",\"lng\":104.06792346,\"lat\":30.67994285,\"issueId\":21}', 19);
INSERT INTO `issue` VALUES (22, '1cd5e4c1-eca4-43ff-bfb2-9395abb5a724arash-asghari-1456878-unsplash.jpg', '标题2', '<p>撒旦发射点</p>', '2020-04-13 10:52:47', '{\"streetNumber\":\"\",\"street\":\"\",\"district\":\"翠屏区\",\"city\":\"宜宾市\",\"province\":\"四川省\",\"lng\":104.677222,\"lat\":28.811399}', 19);
INSERT INTO `issue` VALUES (26, '5d4147e7-c300-4260-8878-c6579deba2c9banner1.jpg', '中国可视化与可视分析大会', '<div class=\"container __reader_view_article_wrap_242088934329187__\" style=\"box-sizing:border-box;padding-right:15px;padding-left:15px;margin-right:auto;margin-left:auto;width:970px;\">\n	<div class=\"head_title\" style=\"box-sizing:border-box;overflow:hidden;\">\n		<h2 style=\"box-sizing:border-box;font-family:&quot;font-weight:normal;line-height:3rem;margin:0px;font-size:28px;text-transform:capitalize;\">\n			会议信息\n		</h2>\n	</div>\n	<div class=\"content_text\" style=\"box-sizing:border-box;padding-top:10px;\">\n		<p style=\"box-sizing:border-box;margin-top:0px;margin-bottom:15px;line-height:2rem;font-size:17px;text-indent:35px;\">\n			中国可视化与可视分析大会(ChinaVis)由我国可视化业界工作者联合发起，宗旨是促进中国及周边地区的可视化与可视分析研究与应用的交流，探讨在大数据时代可视化与可视分析发展的方向与机遇，推动相关研究与应用的发展与进步，推进学科的发展，促进人才培养和交流。会议将搭建与国内外著名专家、企业家、应用部门面对面交流，深入研讨可视化前沿技术及其应用的交流和沟通平台，促进中国可视化与可视分析领域产、学、研、用协同发展新生态的形成。中国可视化与可视分析大会分别于北京（2014年）、天津（2015年）、长沙（2016年）、青岛（2017年）和上海（2018）举办，会议汇集了国内外数百名可视化研究人员与领域专家，取得了圆满成功。\n		</p>\n		<p style=\"box-sizing:border-box;margin-top:0px;margin-bottom:0px;line-height:2rem;font-size:17px;text-indent:35px;\">\n			第六届中国可视化与可视分析大会(ChinaVis\'2019)将于2019年7月21日 - 24日在成都举行。本次大会由中国图象图形学学会主办，可视化与可视分析专委会、四川轻化工大学、西南科技大学、四川大学、电子科技大学、西南交通大学、重庆邮电大学和重庆大学承办，多家赞助单位协办。本次大会内容包括特邀报告、论文报告、海报、展览、及专题研讨。大会将邀请国内外可视化领域知名专家就相关研究与应用的最新进展作大会报告，介绍发展动态，开拓研究思路。欢迎从事可视化与可视分析，以及相关领域研究的专家、学者、学生以及工业界的专家、专业技术人员踊跃投稿与参会。\n		</p>\n	</div>\n</div>\n<div class=\"container\" style=\"box-sizing:border-box;padding-right:15px;padding-left:15px;margin-right:auto;margin-left:auto;width:970px;\">\n	<div class=\" rows head_title\" style=\"box-sizing:border-box;overflow:hidden;\">\n		<div class=\"col-md-8\" style=\"box-sizing:border-box;position:relative;min-height:1px;padding-right:15px;padding-left:0px;float:left;width:626.662px;\">\n		</div>\n	</div>\n</div>', '2020-05-07 05:29:12', '{\"streetNumber\":\"\",\"street\":\"龙门山路\",\"district\":\"成华区\",\"city\":\"成都市\",\"province\":\"四川省\",\"lng\":104.151156,\"lat\":30.635976}', 16);

-- ----------------------------
-- Table structure for organization
